import React, { useContext } from 'react';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import { Helmet } from 'react-helmet-async';
import CheckoutSteps from '../components/CheckoutSteps';
import { Link } from 'react-router-dom';
import { Store } from '../Store';


export default function PlaceOrderScreen() {
    const { state, dispatch: ctxDispatch } = useContext(Store);
    const { cart, userInfo } = state;
  return (
    <div>
      <CheckoutSteps step1 step2 step3 step4></CheckoutSteps>

      <Helmet>
        <title>Preview Order</title>
      </Helmet>
      <h1 className="my-3">Preview Order</h1>
      <Row>
          <Col md={8}>
              <Card className="my-3">
                  <Card.Body>
                      <Card.Title>Shipping</Card.Title>
                      <Card.Text>
                          <strong>Name:</strong> {cart.shippingAddress.fullName}Card <br />
                          <strong>Address:</strong> {cart.shippingAddress.address}, {cart.shippingAddress.city}, {cart.shippingAddress.postalsode} <br />
                          <strong>Brn:</strong> {cart.shippingAddress.brn} <br />
                          <strong>Vat:</strong> vat{cart.shippingAddress.vat} <br />
                      </Card.Text>
                      <Link to="/shipping">Edit</Link>

                  </Card.Body>
              </Card>
              <Card className="my-3">
              <Card.Body>
              <Card.Title>Shipping</Card.Title>
              </Card.Body>

              </Card>

          </Col>
      </Row>
    </div>
  );
}
