import React, { useContext } from 'react';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';
import ListGroup from 'react-bootstrap/ListGroup';
import ListGroupItem from 'react-bootstrap/ListGroupItem';
import Row from 'react-bootstrap/Row';
import { Helmet } from 'react-helmet-async';
import MessageBox from '../components/MessageBox';
import { Store } from '../Store';
import { Link } from 'react-router-dom';

export default function CartScreen() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;

  return (
      <div>
          <Helmet>
              <title>Shopping Cart</title>
          </Helmet>
          <h1>Shopping Cart</h1>
          <Row>
              <Col md={8}></Col>
              {cartItems.length === 0 ? (
                  <MessageBox>
                      Cart is empty. <Link to="/">Go Shopping</Link>
                  </MessageBox>
              ): 
              (
                  <ListGroup>
                      {cartItems.map((item) => (
                          <ListGroupItem key={item._id}>

                              <Row className="align-items-center">
                                  <Col md={4}>
                                      <img src={item.image} alt={item.name} classname="img-fluid img-thumnail"></img>{' '} <Link to={`/product/${item.slug}`}>{item.name}</Link>
                                  </Col>
                                  <Col md={3}>
                                      <Button variant="light" disabled={item.quantity === 1}><i className="fas fa-minus-circle"></i></Button>{' '}
                                      <span>{item.qunatity}</span>{' '}
                                      <Button variant="light" disabled={item.quantity === 1}><i className="fas fa-minus-plus"></i></Button>
                                  </Col>
                                  <Col md={3}>Rs {item.price}</Col>
                                  <Col md={2}>
                                  <Button variant="light"><i className="fas fa-trash"></i></Button>
                                  </Col>
                              </Row>


                          </ListGroupItem>
                      ))}
                  </ListGroup>
              )
              }
              <Col md={4}></Col>
          </Row>

      </div>
  )
}
