import axios from 'axios';
import React, { useContext, useEffect, useReducer } from 'react';
import { PayPalButtons, usePayPalScriptReducer } from '@paypal/react-paypal-js';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import { Helmet } from 'react-helmet-async';
import { Link, useNavigate, useParams } from 'react-router-dom';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { Store } from '../Store';
import { getError } from '../utils';

function reducer(state, action) {

    switch (action.type) {
        case 'FETCH_REQUEST':
            return { ...state, loading: true, error: ''};
        case 'FETCH_SUCCESS':
            return { ...state, loading: false, order: action.payload , error: ''}; 
        case 'FETCH_FAIL':
            return { ...state, loading: false, error: action.payload };

        default:
            return state;    
    }


}

export default function OrderScreen() {


    const { state } = useContext(Store);
    const { userInfo } = state;

    const params = useParams();
    const { id: orderId } = params;
    const navigate = useNavigate();

    const [{ loading, error, order }, dispatch] = useReducer(reducer, {
        loading: true,
        order: {},
        error: '',
    },);

    const [{ isPending }, paypalDispatch] = usePayPalScriptReducer();

    function createOrder(data, actions) {
        return actions.order
            .create({
                purchase_units: [
                    {
                        amount: { value: order.totalPrice},
                    },
                ],
            })
            .then((orderID) => {
                return orderID;
            });
    }

    function onApprove(data, actions) {
        return actions.order.capture().then
    }

    useEffect(() => {

        const fetchOrder = async () => {
            try {
                dispatch({ type: 'FETCH_REQUEST' });
                const { data } = await axios.get(`/api/orders/${orderId}`, {
                    headers: { authorization: ` Bearer ${userInfo.token}` },
                });
                dispatch({ type: 'FETCH_SUCCESS', payload: data });                
            } catch (err) {
                dispatch({ type: 'FETCH_FAIL', payload: getError(err) });
            }
        }



        if (!userInfo) {
            return navigate('/login');
        }
        if (!order._id || (order._id && order._id !== orderId)) {
            fetchOrder();
        } else {
            const loadPaypalScript = async () => {
                const { data: clientId } = await axios.get('/api/keys/paypal', {
                    headers: { authorization: `Bearer ${userInfo.token}`},
                });
                paypalDispatch({
                    type: 'resetOptions',
                    value: {
                        'clienst-id': clientId,
                        currency:'MUR',
                    }, 
                });
                paypalDispatch({ type: 'setLoadingStatus', value: 'pending' });

            }
            loadPaypalScript();
        }


    },[order, userInfo, orderId, navigate, paypalDispatch]);
  return loading ? (
    <LoadingBox></LoadingBox>
    ) : error ? (
    <MessageBox variant="danger">{error}</MessageBox>
    ) : (
        <div>
            <Helmet>
                <title>Order {orderId}</title>
            </Helmet>
            <h1 className="my-3">Order {orderId}</h1>
            <Row>
                <Col md={8}>
                    <Card className="mb-3">
                        <Card.Body>
                            <Card.Title>Shipping</Card.Title>
                            <Card.Text>
                            <strong>Name:</strong> {order.shippingAddress.fullName}
                            <br />
                            <strong>Address:</strong> {order.shippingAddress.address},{' '}
                            {order.shippingAddress.city}, {order.shippingAddress.postalcode}{' '}
                            <br />
                            <strong>Brn:</strong> {order.shippingAddress.brn} <br />
                            <strong>Vat:</strong> vat{order.shippingAddress.vat} <br />
                            </Card.Text>
                            {order.isDelivered ? (
                                <MessageBox variant="success">
                                    Delivered at {order.deliveredAt}
                                </MessageBox>
                            ) : (
                                <MessageBox variant="danger">Not Delivered</MessageBox>
                            )}
                        </Card.Body>

                    </Card>
                    <Card className="mb-3">
                        <Card.Body>
                            <Card.Title>Payment</Card.Title>
                            <Card.Text>
                                <strong>Method:</strong>{order.paymentMethod}
                            </Card.Text>
                            {order.isPaid ? (
                                <MessageBox variant="success">
                                    Paid at {order.paidAt}
                                </MessageBox>
                            ) : (
                                <MessageBox variant="danger">Not Paid</MessageBox>
                            )}
                        </Card.Body>

                    </Card>
                    <Card className="mb-3">
                    <Card.Body>
                    <Card.Title>Items</Card.Title>
                    <ListGroup variant="flush">
                        {order.orderItems.map((item) => (
                        <ListGroup.Item key={item._id}>
                            <Row className="align-items-center">
                            <Col md={6}>
                                <img
                                src={item.image}
                                alt={item.name}
                                className="img-fluid rounded img-thumnail"
                                ></img>{' '}
                                <Link to={`/product/${item.slug}`}>{item.name}</Link>
                            </Col>
                            <Col md={3}>
                                <span>{item.quantity}</span>
                            </Col>
                            <Col md={3}>
                                <span>Rs {item.price}</span>
                            </Col>
                            </Row>
                        </ListGroup.Item>
                        ))}
                    </ListGroup>
                    </Card.Body>
                    </Card>

                </Col>
                <Col md={4}>
          <Card>
            <Card.Body>
              <Card.Title>Order Summary</Card.Title>
              <ListGroup variant="flush">
                <ListGroup.Item>
                  <Row>
                    <Col>Items</Col>
                    <Col>Rs {order.itemsPrice.toFixed(2)}</Col>
                  </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row>
                    <Col>Shipping</Col>
                    <Col>Rs {order.shippingPrice.toFixed(2)}</Col>
                  </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row>
                    <Col>Vat</Col>
                    <Col>Rs {order.vatPrice.toFixed(2)}</Col>
                  </Row>
                </ListGroup.Item>
                <ListGroup.Item>
                  <Row>
                    <Col>
                      <strong> Order Total</strong>
                    </Col>
                    <Col>
                      <strong>Rs {order.totalPrice.toFixed(2)}</strong>
                    </Col>
                  </Row>
                </ListGroup.Item>
                {!order.isPaid && (
                    <ListGroup.Item>
                        {isPending ? (
                            <LoadingBox />
                        ) : (
                            <div>
                                <PayPalButtons
                                createOrder={createOrder} onApprove={onApprove} onError={onError} >

                                </PayPalButtons>
                            </div>
                        )}
                    </ListGroup.Item>
                )}
              </ListGroup>
            </Card.Body>
          </Card>
        </Col>
            </Row>
        </div>
    );


